mapper.insert:
INSERT INTO sys_user
(
	USER_ID,
	, USER_NAME
	, PASS_WORD
	, SALT
	, EMAIL
	, MOBILE
	, STATUS
	, CREATE_USER_ID
	, CREATE_TIME
)
VALUES
(
	#{userId}
	, #{userName}
	, #{passWord}
	, #{salt}
	, #{email}
	, #{mobile}
	, #{status}
	, #{createUserId}
	, #{createTime}
)

mapper.update:
UPDATE sys_user
USER_NAME = #{userName}, PASS_WORD = #{passWord}, SALT = #{salt}, EMAIL = #{email}, MOBILE = #{mobile}, STATUS = #{status}, CREATE_USER_ID = #{createUserId}, CREATE_TIME = #{createTime},
WHERE USER_ID = #{userId}
